import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-data',
  templateUrl: './add-data.component.html',
  styleUrls: ['./add-data.component.css']
})
export class AddDataComponent {

  get: boolean=false;
  myForm: FormGroup;  // <-- Declare the form group

  constructor(private formBuilder: FormBuilder) { 
    // Initialize the form using FormBuilder (use Validators if necessary)
    this.myForm = this.formBuilder.group({
      name: ['', Validators.required],         // A form control for 'name' with validation
      age: ['', Validators.required],          // A form control for 'age' with validation
      salary: ['', Validators.required],       // A form control for 'salary' with validation
      department: ['', Validators.required]    // A form control for 'department' with validation
    });
  }

  // Submit method
  onSubmit() {
    if (true) {  // <-- Check if the form is valid before submission
      console.log(this.myForm.value);  // This logs the form's value
      this.get=true;
    } else {
      console.log('Form is invalid');
      console.log(this.myForm);
    }
  }

}
