import { Component, OnInit } from '@angular/core';
import { ServiceService } from '../Service/service.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-edit-data',
  templateUrl: './edit-data.component.html',
  styleUrls: ['./edit-data.component.css']
})
export class EditDataComponent implements OnInit{

  get: boolean=false;
  myForm!: FormGroup;  // <-- Declare the form group
  submittedData:any = null;
  backEndData:any=null;
  id:String="66fb8be8d286b55cab213603";


  constructor(private formBuilder: FormBuilder,private service:ServiceService) { 
    this.service.getById(this.id).subscribe((data: any) => {
      // console.log(data);
      this.backEndData=data;
      console.log(this.backEndData);
      return this.backEndData
    })


  }


  ngOnInit(): void {
    this.myForm = this.formBuilder.group({
      name: [this.backEndData.name, Validators.required],         // A form control for 'name' with validation
      age: ['', Validators.required],          // A form control for 'age' with validation
      salary: ['', Validators.required],       // A form control for 'salary' with validation
      department: ['', Validators.required]    // A form control for 'department' with validation
    });

  }

  // Submit method
  onSubmit():void {
    if (this.myForm.valid) {  // <-- Check if the form is valid before submission
      console.log(this.myForm.value);  // This logs the form's value
      this.submittedData = this.myForm.value;

      this.service.addData(this.myForm.value).subscribe(data=>{
        console.log(data);
        return this.myForm.value
      })

    } else {
      console.log('Form is invalid');
      console.log(this.myForm);
    }
  }


}
